parameters.param:
#!/bin/bash
function rspecTest { cat <<EOF describe Hash do before do @hash = Hash.new({:hello => 'world'}) end
it "should return a blank instance" do Hash.new.should == {} end
it "hash the correct information in a key" do @hash[:hello].should == 'world' end end EOF }
function anotherRspecTest { cat <<EOF describe 'calculation' do it "should compute 1+1" do expect(1+1).to eq(2) end end EOF }
mkdir -p spec rspecTest > spec/example_spec.rb anotherRspecTest > spec/another_spec.rb
if ! type gem &> /dev/null; then apt-get update apt-get -qy install ruby fi
gem install rake gem install rspec -v 3.1.0 gem install rspec_junit_formatter
